import React from "react";
import { useState } from "react";
import { Box, HStack, Image } from "@chakra-ui/react";
import logo from "../images/Asset 16@4x.png"

const links = [
    {
      name: "Home",
      url: "/#home",
      anchor: "home",
    },
    {
      name: "About",
      url: "/#about",
      anchor: "about",
    },
    {
      name: "Menu",
      url: "/#menu",
      anchor: "menu",
    },
    {
      name: "Reservations",
      url: "/#reserve",
      anchor: "reserve",
    },
    {
      name: "Order online",
      url: "/#order",
      anchor: "order",
    },
    {
      name: "Login",
      url: "/#login",
      anchor: "login",
    },
  ];



const Header = () => {
    const [hover, setHover] = useState(false);

    const handleMouseEnter = () => {
    setHover(true);
    };
    const handleMouseLeave = () => {
    setHover(false);
    };

    return (
        <Box
            position="fixed"
            top={0}
            left={0}
            right={0}
            translateY={0}
            transitionProperty="transform"
            transitionDuration=".3s"
            transitionTimingFunction="ease-in-out"
            backgroundColor="whiteAlpha.50"
        >
            <Box
            margin="0 auto"
            >
                <HStack
                    px={16}
                    py={4}
                    justifyContent="space-between"
                    alignItems="center"
                    margin={"0 80px"}
                    // marginRight={"60px"}
                >
                    <nav>
                        <HStack spacing={8}>
                            <Image src={logo} alt="Logo" height={"60px"} />
                        </HStack>
                    </nav>
                    <nav>
                        <HStack spacing={8}>
                            {links.map(link =>
                            <Box rounded={"md"} padding={"5px"} bgColor={hover?"#495E57":"white"} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
                                <a key={link.anchor} href={link.url} > {link.name}</a>
                            </Box>)
                            }
                        </HStack>
                    </nav>
                </HStack>
            </Box>
        </Box>
    );
};

export default Header;
